mapper.select:
SELECT article_id, category_id, article_type, user_id, title,
contents, is_open, is_bold, is_top, is_deleted, create_time,
update_time
FROM blog.article
WHERE user_id = #{user_id}
 AND is_open = #{is_open}

mapper.update:
UPDATE blog.article
SET category_id = #{category_id},article_type = #{article_type},title = #{title},contents = #{contents},
is_open = #{is_open},is_bold = #{is_bold},is_top = #{is_top}
WHERE article_id = #{article_id} AND user_id = #{user_id}
